import 'regenerator-runtime/runtime';
import React, { Component } from 'react';
import Transfer from './Transfer';
import logo from './assets/logo.svg';
import nearlogo from './assets/gray_near_logo.svg';
import near from './assets/near.svg';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      login: false,
      speech: null
    }
    this.signedInFlow = this.signedInFlow.bind(this);
    this.requestSignIn = this.requestSignIn.bind(this);
    this.requestSignOut = this.requestSignOut.bind(this);
    this.signedOutFlow = this.signedOutFlow.bind(this);
    this.changeGreeting = this.changeGreeting.bind(this);
  }

  componentDidMount() {
    let loggedIn = this.props.wallet.isSignedIn();
    if (loggedIn) {
      this.signedInFlow();
    } else {
      this.signedOutFlow();
    }
  }

  async signedInFlow() {
    console.log("come in sign in flow")
    this.setState({
      login: true,
    })
    const accountId = await this.props.wallet.getAccountId()
    if (window.location.search.includes("account_id")) {
      window.location.replace(window.location.origin + window.location.pathname)
    }
    await this.welcome();
  }

  async welcome() {
    const response = await this.props.contract.welcome({ account_id: accountId });
    this.setState({speech: response.text});
  }

  async requestSignIn() {
    const appTitle = 'NEAR React template';
    await this.props.wallet.requestSignIn(
      window.nearConfig.contractName,
      appTitle
    )
  }

  requestSignOut() {
    this.props.wallet.signOut();
    setTimeout(this.signedOutFlow, 500);
    console.log("after sign out", this.props.wallet.isSignedIn())
  }

  async changeGreeting() {
    await this.props.contract.setGreeting({ message: 'howdy' });
    await this.welcome();
  }

  signedOutFlow() {
    if (window.location.search.includes("account_id")) {
      window.location.replace(window.location.origin + window.location.pathname)
    }
    this.setState({
      login: false,
      speech: null
    })
  }

  render() {
    let style = {
      fontSize: "1.5rem",
      color: "#0072CE",
      textShadow: "1px 1px #D1CCBD"
    }
    return (
      <React.Fragment>
        <div>
          {/* add menu component only if login is true */}
        </div>
        <div>
          <div className="App-header">
            <div className="image-wrapper">
              <img className="logo" src={nearlogo} alt="NEAR logo" />
              <p style={style}>{this.state.speech}</p>
            </div>
            <div>
              {this.state.login ? 
                <div>
                  <div>
                    <button onClick={this.changeGreeting}>Change greeting</button>
                  </div>
                  <div className="Transfer-module">
                    <Transfer />
                  </div>
                  <div>
                    <button onClick={this.requestSignOut}>Log out</button>
                  </div>
                </div>
                : <button onClick={this.requestSignIn}>Log in with NEAR</button>}
            </div>
          </div>
        </div>
      </React.Fragment>
    )
  }

}

export default App;
